                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        package practise;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;
public class P1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.setProperty("webdriver.chrome.driver", "C:/Users/SILPA/workspace/Mytest/Drivers/chromedriver.exe");
		WebDriver driver=new ChromeDriver();
		driver.get("http://www.store.demoqa.com/");
		String Tittle=driver.getTitle();
		System.out.println(Tittle);
		int len=Tittle.length();
		System.out.println(len);
		if(Tittle==driver.getCurrentUrl())
		{
			System.out.println("URL loaded is correct");
		}
		else
			System.out.println("URL loaded is not correct");
		String pagesource=driver.getPageSource();
		System.out.println(pagesource);
		System.out.println(pagesource.length());
		driver.close();
		

	}

}
